function setDurations () {
    Durations = []
    for (let index = 0; index < 6; index++) {
        for (let index = 0; index < 6; index++) {
            Durations.push(music.beat(BeatFraction.Whole))
        }
        Durations.push(music.beat(BeatFraction.Double))
    }
}
controller.left.onEvent(ControllerButtonEvent.Pressed, function () {
    pressTile(3)
})
controller.down.onEvent(ControllerButtonEvent.Pressed, function () {
    pressTile(2)
})
function AddNotesB () {
    for (let index = 0; index < 2; index++) {
        for (let Note2 of [392, 392, 349, 349, 330, 392, 294]) {
            Notes.push(Note2)
        }
    }
}
function AddNotesA () {
    for (let Note2 of [262, 262, 392, 392, 440, 523, 392]) {
        Notes.push(Note2)
    }
    for (let Note2 of [349, 349, 330, 330, 294, 392, 262]) {
        Notes.push(Note2)
    }
}
function setNotes () {
    Notes = []
    AddNotesA()
    AddNotesB()
    AddNotesA()
    Notes.push(262)
    Durations.push(music.beat(BeatFraction.Double))
}
controller.B.onEvent(ControllerButtonEvent.Pressed, function () {
    if (Volume >= 32) {
        Volume += -32
        music.setVolume(Volume)
    }
})
function pressTile (Dir: number) {
    if (Tiles.length > 0 && !(Dead)) {
        OldestTile = Tiles[0]
        if (sprites.readDataNumber(OldestTile, "Direction") == Dir) {
            timer.background(function () {
                music.playTone(sprites.readDataNumber(OldestTile, "Note"), sprites.readDataNumber(OldestTile, "Duration"))
            })
            info.changeScoreBy(Multiplier)
            sprites.setDataBoolean(OldestTile, "Pressed", true)
            OldestTile.destroy(effects.fountain, 100)
            devnull = Tiles.removeAt(0)
        } else {
            timer.background(function () {
                music.playTone(311, music.beat(BeatFraction.Half))
            })
            info.changeLifeBy(-1)
            scene.cameraShake(4, 500)
            OldestTile.startEffect(effects.fire, 500)
        }
    }
}
controller.A.onEvent(ControllerButtonEvent.Pressed, function () {
    if (Volume <= 224) {
        Volume += 32
        music.setVolume(Math.min(255, Volume))
    }
})
controller.right.onEvent(ControllerButtonEvent.Pressed, function () {
    pressTile(1)
})
function displayTime () {
    info.startCountdown((game.runtime() - StartTime) / 1000)
}
sprites.onDestroyed(SpriteKind.Tile, function (sprite) {
    if (!(sprites.readDataBoolean(sprite, "Pressed"))) {
        timer.background(function () {
            music.playTone(311, music.beat(BeatFraction.Half))
        })
        info.changeLifeBy(-1)
        scene.cameraShake(4, 500)
    }
    devnull = Tiles.removeAt(Tiles.indexOf(sprite))
})
controller.up.onEvent(ControllerButtonEvent.Pressed, function () {
    pressTile(0)
})
info.onLifeZero(function () {
    Dead = true
    timer.after(2000, function () {
        game.over(false)
    })
})
